mapper.insert:
INSERT INTO xbt_module_info_detail(
	MODULE_ID
	, STUDY_LVL
	, BAG_SCAN_ID
	, UNIT_ID
	, UNIT_GROUP_CD
	, INSERT_ID
	, INSERT_DATE
)VALUES(
	#{moduleId}
	,#{studyLvl}
	,#{bagScanId}
	,#{unitId}
	,#{unitGroupCd}
	,#{insertId}
	,now()
)

mapper.update:

UPDATE 
	XBT_MODULE_INFO_MASTER 
SET 
	MODULE_NM = #{moduleNm}
	, MODULE_DESC = #{moduleDesc}
