mapper.insert:
INSERT INTO xbt_SYSTEM_MESSAGE
(
	TITLE
	, CONTENTS
	, INSERT_ID
	, INSERT_DATE
	, UPDATE_ID
	, UPDATE_DATE
)
VALUES(
	,#{title}
	,#{contents}
	,#{insertId}
	,now()
)

mapper.update:

UPDATE xbt_NOTICE
SET 
	TITLE = #{title}
	, CONTENTS = #{title}
	, UPDATE_ID = #{updateId}
	, UPDATE_DATE = now()
WHERE NOTICE_ID = 0;
