mapper.insert:
INSERT	INTO XBT_STU_MENU(
	P_MENU_CD,
	MENU_CD,
	MENU_NAME,
	MENU_URL,
	MENU_LEVEL,
	USE_YN,
	MENU_ORDER,
	LANGUAGE_CODE,
	MENU_TYPE,
	INSERT_ID,
	INSERT_DATE
)VALUES(
	#{pmenuCd}
	,#{menuCd}
	,#{menuName}
	,#{menuUrl}
	,'1'
	,'Y'
	,#{menuOrder}
	,#{languageCode}
	,#{menuType}
	,#{insertId}
	,now()
)

mapper.update:

UPDATE XBT_STU_MENU
SET 
	P_MENU_CD = #{pmenuCd}
	, MENU_CD = #{menuCd}
	, MENU_NAME = #{menuName}
	, USE_YN = #{useYn}
	, MENU_ORDER = #{menuOrder}
	, MENU_TYPE = #{menuType}
	, UPDATE_ID = #{updateId}
	, UPDATE_DATE = now()
WHERE MENU_CD = #{menuCd}
AND LANGUAGE_CODE = #{languageCode}

mapper.delete:
DELETE FROM XBT_STU_MENU 
WHERE MENU_CD = #{menuCd}
AND LANGUAGE_CODE = #{languageCode}
