mapper.update:

UPDATE 
	XBT_COMMON_CODE
SET 
	LANGUAGE_CODE = #{languageCode}	
	,GROUP_ID	= #{groupId}	
	,CODE_ID	= #{codeId}	
	,SORT_ORDER	= #{sortOrder}	
	,CODE_NAME	= #{codeName}	
	,REMARKS	= #{remarks}	
	,USE_YN	= #{useYn}	
	,UPDATE_ID	= #{updateId}	
	,UPDATE_DATE	= now()	
	,CODE_DESC	= #{codeDesc}	
WHERE CODE_ID = #{codeId}

mapper.select:
SELECT GROUP_ID ,CODE_NAME, CODE_VALUE, CODE_DESC ,USE_YN, LANGUAGE_CODE
FROM XBT_SYSTEM_CODE
WHERE
LANGUAGE_CODE= #{languageCode}


		AND GROUP_ID = #{groupId}


AND USE_YN  = 'Y'

SELECT GROUP_ID ,CODE_NAME, CODE_VALUE, CODE_DESC , USE_YN, LANGUAGE_CODE
FROM XBT_SYSTEM_CODE
WHERE
GROUP_ID = #{groupId} 
AND CODE_ID = #{codeId}
AND LANGUAGE_CODE= #{languageCode}
AND USE_YN  = 'Y'
