mapper.select:
SELECT 
	COUNT(PROGRESS_NO) 
FROM XBT_STU_EVALUATION_PROGRESSING
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}

mapper.update:

UPDATE 
	XBT_BASELINE_EVALUATION 
SET END_YN = 'N'
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}

mapper.select:
SELECT ACTION_DIV = #{userActionDiv} as answer 
FROM XBT_STU_EVALUATION_PROGRESSING 
WHERE 
MODULE_ID = #{moduleId} 
and PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}
AND BAG_SCAN_ID = #{bagScanId}
AND END_YN = 'N'

mapper.update:

UPDATE 
	XBT_STU_EVALUATION_PROGRESSING 
SET 
	USER_ACTION_DIV = #{userActionDiv},
	GAIN_SCORE = #{gainScore}
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}
AND BAG_SCAN_ID = #{bagScanId}

mapper.select:
SELECT SUM(GAIN_SCORE) AS gainScore 
FROM XBT_STU_EVALUATION_PROGRESSING 
WHERE 
MODULE_ID = #{moduleId} 
AND PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}
AND END_YN = 'N'

mapper.update:

UPDATE 
	XBT_BASELINE_EVALUATION 
SET 
	GAIN_SCORE = #{gainScore},
	PASS_YN = #{passYn}
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}
